package config

import (
	"encoding/json"
	"log"
	"os"
	"runtime/debug"
	"strconv"
	"strings"
	"sync"

	"github.com/layou233/ZBProxy/common/set"
	"github.com/layou233/ZBProxy/version"

	"github.com/fatih/color"
	"github.com/zhangyunhao116/fastrand"
)

const DefaultMotd = `data:image/png;base64,`

var (
	Config     configMain
	reloadLock sync.Mutex
)

func LoadConfig() {
	configFile, err := os.ReadFile("ZBProxy.json")
	if err != nil {
		if os.IsNotExist(err) {
			log.Println("配置文件不存在。正在生成一个新的...")
			generateDefaultConfig()
			goto success
		} else {
			log.Panic(color.HiRedString("加载配置时出现意外错误: %s", err.Error()))
		}
	}

	err = json.Unmarshal(configFile, &Config)
	if err != nil {
		log.Panic(color.HiRedString("配置格式错误: %s", err.Error()))
	}

success:
	LoadLists(false)
	log.Println(color.HiYellowString("已成功从文件加载配置。"))
}

func generateDefaultConfig() {
	file, err := os.Create("ZBProxy.json")
	if err != nil {
		log.Panic("创建配置文件失败：", err.Error())
	}
	Config = configMain{
		Services: []*ConfigProxyService{
			{
				Name:          "HypixelDefault",
				TargetAddress: "mc.hypixel.net",
				TargetPort:    25565,
				Listen:        25565,
				Flow:          "auto",
				Minecraft: minecraft{
					EnableHostnameRewrite: true,
					OnlineCount: onlineCount{
						Max:            114514,
						Online:         -1,
						EnableMaxLimit: false,
					},
					MotdFavicon:     "{DEFAULT_MOTD}",
					MotdDescription: "§d{NAME}§e service is working on §a§o{INFO}§r\n§c§lProxy for §6§n{HOST}:{PORT}§r",
				},
			},
		},
		Lists: map[string]set.StringSet{
			//"test": {"foo", "bar"},
		},
	}
	newConfig, _ := json.MarshalIndent(Config, "", "    ")
	_, err = file.WriteString(strings.ReplaceAll(string(newConfig), "\n", "\r\n"))
	file.Close()
	if err != nil {
		log.Panic("保存配置文件失败：", err.Error())
	}
}

func LoadLists(isReload bool) bool {
	reloadLock.Lock()
	defer reloadLock.Unlock()
	var config configMain
	if isReload {
		configFile, err := os.ReadFile("ZBProxy.json")
		if err != nil {
			if os.IsNotExist(err) {
				log.Println(color.HiRedString("重新加载失败：配置文件不存在."))
			} else {
				log.Println(color.HiRedString("重新加载配置时出现意外错误: %s", err.Error()))
			}
			return false
		}

		err = json.Unmarshal(configFile, &config)
		if err != nil {
			log.Println(color.HiRedString("无法重新加载：配置格式错误: %s", err.Error()))
			return false
		}
	} else {
		config = Config
	}

	for _, s := range config.Services {
		if s.Minecraft.MotdFavicon == "{DEFAULT_MOTD}" {
			s.Minecraft.MotdFavicon = DefaultMotd
		}
		s.Minecraft.MotdDescription = strings.NewReplacer(
			"{INFO}", "ZBProxy "+version.Version,
			"{NAME}", s.Name,
			"{HOST}", s.TargetAddress,
			"{PORT}", strconv.Itoa(int(s.TargetPort)),
		).Replace(s.Minecraft.MotdDescription)

		if samples := s.Minecraft.OnlineCount.Sample; samples != nil {
			var convertedSamples []Sample
			switch samples := samples.(type) {
			case map[string]any:
				convertedSamples = make([]Sample, 0, len(samples))
				for uuid, name := range samples {
					convertedSamples = append(convertedSamples, Sample{
						Name: name.(string),
						ID:   uuid,
					})
				}

			case []any:
				convertedSamples = make([]Sample, 0, len(samples))
				var u [16]byte
				var dst [36]byte
				for i, sample := range samples {
					// generate random UUID with ZBProxy signature
					fastrand.Read(u[:])
					u[0] = byte(i)
					u[1] = '$'
					u[2] = 'Z'
					u[3] = 'B'
					u[4] = '$'

					// marshal UUID string
					const hexTable = "0123456789abcdef"
					dst[8] = '-'
					dst[13] = '-'
					dst[18] = '-'
					dst[23] = '-'
					for i, x := range [16]byte{
						0, 2, 4, 6,
						9, 11,
						14, 16,
						19, 21,
						24, 26, 28, 30, 32, 34,
					} {
						c := u[i]
						dst[x] = hexTable[c>>4]
						dst[x+1] = hexTable[c&0x0F]
					}

					convertedSamples = append(convertedSamples, Sample{
						Name: sample.(string),
						ID:   string(dst[:]),
					})
				}

			default:
				log.Println(color.HiMagentaString(
					"配置重新加载：无法重新加载样本：未知样本输入类型: %T", samples))
				return false
			}
			s.Minecraft.OnlineCount.Sample = convertedSamples
		}
	}

	Config = config
	debug.FreeOSMemory()
	return true
}
